mapper.select:
select * from pay_merchant_bank_cards
where pb_bankCardId in

	 #{bankCardId}

  SELECT * FROM pay_merchant_bank_cards 
	
	  
		 pb_bankCardId=#{bankCardId}  
	   
	  
